import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

const RegistrationPage = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
  });

  const [registeredUsers, setRegisteredUsers] = useState([]); // Состояние для хранения зарегистрированных пользователей

  const history = useHistory();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Создайте объект пользователя из данных формы
    const newUser = {
      username: formData.username,
      email: formData.email,
      password: formData.password,
    };

    // Добавьте нового пользователя к списку зарегистрированных пользователей
    setRegisteredUsers([...registeredUsers, newUser]);

    // Очистите форму
    setFormData({
      username: '',
      email: '',
      password: '',
    });

    console.log('Зарегистрированные пользователи:', registeredUsers);
  };

  return (
    <div>
      <h2>Регистрация</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="username">Имя пользователя:</label>
          <input
            type="text"
            id="username"
            name="username"
            value={formData.username}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="password">Пароль:</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit">Зарегистрироваться</button>
      </form>
    </div>
  );
};

export default RegistrationPage;
