import React from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Home from "./pages/Home";
import Contacts from "./pages/Contacts";
import LoginPage from './LoginPage'; // Добавьте компонент страницы логина
import RegistrationPage from './RegistrationPage';

const App = () => {
  const [authInfo, setAuthInfo] = useState({
    isAuthenticated: false,
    username: '',
  });

  return (
    <Router>
      <Routes>
      <Route
          path="/"
          element={<Home isAuthenticated={authInfo.isAuthenticated} username={authInfo.username} />}
        />
        <Route
          path="/login"
          element={<LoginPage setAuthInfo={setAuthInfo} />}
        />
        <Route path="/" element={<Home />} />
        <Route path="/contacts" element={<Contacts />} />
        <Route path="/login" element={<LoginPage/>} />
          <Route path="/registration" element={<RegistrationPage/>} />

        {/* Другие маршруты, если необходимо */}
      </Routes>
    </Router>
  );
};

export default App;
